from twnet_parser.packer import pack_int

class MsgSnapSingle(PrettyPrint):
    def __init__(
            self,
            tick: int = 0,
            delta_tick: int = 0,
            crc: int = 0,
            data: bytes = b'\x00'
    ) -> None:
        self.message_name = 'snap_single'
